}

function Body(){
   const [text, setText] = useState(0);
   const handleOnChange = (e) => {
    setText(e.target.value);
   }
   const handleOnClicked = () => {
    alert(text);
   }
    return (
        <div>
            <div>
                <input value={text} onChange={handOnChange}/>
                <button onClick = {handleOnClicked}>작성완료</button>
            </div>
        </div>
    );
}
export default Body;
