    return(
        <div className="Viewer">
            <div className="viewer_title">
                오늘의 감정
            </div>
            <div className={["img_section", `img_section_${emotionId}`].join(" ")}>
                <img alt={`emotion${emotionId}`} src={getEmotionImgById(emotionId)} />    
            </div>
            <div className="viewer_title">오늘의 일기</div>  
            <div className="text_area"
                text={content}
            />
        </div>
    );
}

