    setText(e.target.value);
   }
   const handleOnClick = () => {
    alert(text);
   }
    return (
        <div>
                <input value={text} onChange={handleOnChange}/>
                <button onClick = {handleOnClick}>작성완료</button>
        </div>
    );
}
