    setText(e.target.value);
   }
   const handleOnClick = () => {
    alert(text);
    textRef.current.value="";
   };
   
    return (
        <div>
                <input value={text} onChange={handleOnChange} />
                <button onClick = {handleOnClick}>작성 완료</button>
